    type: "alicloud",
    path: "alicloud",
});
const alicloudAuthBackendRole = new vault.alicloud.AuthBackendRole("alicloudAuthBackendRole", {
    backend: alicloudAuthBackend.path,
    role: "example",
    arn: "acs:ram:123456:tf:role/foobar",
});
